import React, { useState, useEffect } from 'react'
import styles from './SlideShow.module.scss'

const SlideShow = ({ children, showElements }) => {
  const [ rootElement, setRootElement ] = useState(null)

  useEffect(() => {
    setRootElement(document.getElementById(styles.root))
  }, [ rootElement ])

  return (
    <div id={ styles.root }>
      <div className={ styles.container } style={ { width: rootElement.offsetWidth / showElements } }>
        { children }
      </div>
    </div>
  )
}

export default SlideShow
