import React, { useEffect, useState } from 'react'
import styles from './Slot.module.scss'
import PropTypes from 'prop-types'

const Slot = ({ item, index }) => {
  const ref = React.createRef()
  const [ childNodes, setChildNodes ] = useState([])

  useEffect(() => {
    setChildNodes(ref.current.childNodes)
    setHeight(ref)
  }, [ ref ])

  const setHeight = ({ current }) => {
    if (!current) return
    else {
      const rootStyle = getComputedStyle(current)
      const padding = +rootStyle.paddingTop.replace('px', '') + (+rootStyle.paddingBottom.replace('px', ''))
      const { childNodes } = current
      if (childNodes.length) {
        console.log(padding, childNodes[ 1 ].offsetHeight.getBoundingClientRect())
        let totalHeight = item.isActive ? childNodes[ 0 ].offsetHeight + childNodes[ 1 ].offsetHeight : childNodes[ 0 ].offsetHeight
        totalHeight += padding
        current.style.height = totalHeight + 'px'
      }
    }
  }

  return (
    <div className={ styles.root + (item.isActive ? ' ' + styles.isActive : '') }
         custom-id={ index }
         ref={ ref }>
      <div className={ styles.title } custom-id={ index }>{ item.title } { index }</div>
      <div className={ styles.description } custom-id={ index }>{ item.description }</div>
    </div>
  )
}

Slot.propTypes = {
  item: PropTypes.object,
}

export default Slot
