import React from 'react'
import PropTypes from 'prop-types'
import styles from './DropDown.module.scss'

const DropDown = ({ items }) => {

  const handleClick = ({ target }) => {
    const id = target.getAttribute('custom-id')
    console.log(id)
  }

  return (
    <div className={ styles.root } onClick={ handleClick }>
      { items.map((item, key) =>
        <div className={ styles.item + (item.isActive ? ' ' + styles.isActive : '') } key={ key } custom-id={ key }>
          <div className={ styles.title } custom-id={ key }>{ item.title }</div>
          <div
            className={ styles.description }
            custom-id={ key }>{ item.description }</div>
        </div>
      ) }
    </div>
  )
}

DropDown.propTypes = {
  items: PropTypes.arrayOf(PropTypes.object)
}

export default DropDown
