import { createReducer } from "../../utils/store"

import { CHANGE_VALUE } from './actions'

const defaultProps = {
  value: 'Hello'
}

export default createReducer(defaultProps, {
  [ CHANGE_VALUE ]: (state, { value }) => ({ ...state, value })
})
