import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import stylesHamburger from './hamburger.module.scss'
import styles from './TopSidebar.module.scss'


const TopSidebar = (props) => {
  useEffect(() => {
    console.log(stylesHamburger)
  }, [])

  // const toggleMobileMenu = () =>  {
  //   const { toggleMobileMenu, mobileMenuStatus } = props
  //   let rootElem = document.getElementById('root')
  //   if (!mobileMenuStatus) {
  //     rootElem.style.overflowY = 'hidden'
  //     rootElem.style.height = window.innerHeight + 'px'
  //   }
  //   else {
  //     rootElem.style.overflowY = 'auto'
  //     rootElem.style.height = 100 + '%'
  //
  //   }
  //   toggleMobileMenu(!mobileMenuStatus)
  // }

  return (
    <div className='top-side-bar'>
      <button
        className={ !props.mobileMenuStatus ? { ...stylesHamburger[ 'hamburger' ], ...stylesHamburger[ 'hamburger--slider' ], ...stylesHamburger[ 'is-active' ] } : 'hamburger hamburger--slider' }
        type="button"
        //onClick={ toggleMobileMenu }
      >
        <span className="hamburger-box"><span className="hamburger-inner"/></span>
      </button>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    windowInnerWidth: state.window.size.windowInnerWidth,
  }
}

export default connect(mapStateToProps)(TopSidebar)
