import React, { useState, useEffect } from 'react'
import styles from './SlideShow.module.scss'

const SlideShow = ({ children = [], showElements, animationTime = 300, classNameContainer, slideHeight, idRoot }) => {

  const [ rootElement, setRootElement ] = useState({})
  const [ containerElement, setContainerElement ] = useState({})
  const [ copyChildren, setCopyChildren ] = useState([])
  const [ slideOption, setSlideOption ] = useState({
    totalSlide: children.length,
    currentIndex: 0,
    currentElementIndex: 0
  })
  const [ slideIsOver, setSlideIsOver ] = useState(false)

  useEffect(() => {
    setRootElement(document.getElementById(idRoot))
  }, [ rootElement ])

  useEffect(() => {
    let container = document.querySelector('.' + classNameContainer + '>div')
    setContainerElement(container)
    let slideItem = document.querySelectorAll('.' + classNameContainer + '>div>div')
    slideItem.forEach(item => {
      console.log(item)
      item.style.width = rootElement.offsetWidth / showElements + 'px'
    })

  }, [ containerElement ])

  useEffect(() => {
    setCopyChildren(children.slice())
  }, [ children ])

  const changeSlide = slide => {
    if (slideIsOver) return false
    else {
      setSlideIsOver(true)
      let newChildrenArr = []
      let currentIndex = slideOption.currentIndex + (slide)
      let currentElementIndex = slideOption.currentElementIndex + (slide)
      currentElementIndex = currentElementIndex < 0 ? copyChildren.length - 1 : currentElementIndex > (copyChildren.length - 1) ? 0 : currentElementIndex

      if (slide === 1) {
        let firstChild = copyChildren[ 0 ]
        newChildrenArr = [ ...copyChildren.slice(1, copyChildren.length), firstChild ]
      }
      else {
        let lastChild = copyChildren[ copyChildren.length - 1 ]
        newChildrenArr = [ lastChild, ...copyChildren.slice(0, copyChildren.length - 1) ]
      }

      setSlideOption({ ...slideOption, currentIndex, currentElementIndex })

      setTimeout(() => {
        setCopyChildren(newChildrenArr)
        //document.querySelector('.' + containerElement).style.marginLeft = (currentIndex * (rootElement.offsetWidth / showElements) + 'px')
        containerElement.style.marginLeft = (currentIndex * (rootElement.offsetWidth / showElements) + 'px')
        setSlideIsOver(false)
      }, slide === 1 ? animationTime : 0)
    }
  }

  const setCurrentSlide = index => {
    if (slideOption.currentElementIndex === index) return
    else {
      //let container = document.querySelector('.' + styles.container)
      containerElement.style.transition = 'none'
      let currentIndex = index - slideOption.currentElementIndex

      let newChildrenArr = []
      if (currentIndex >= 1) {
        let firstChild = copyChildren.slice(0, Math.abs(currentIndex))
        console.log(firstChild)
        newChildrenArr = [ ...copyChildren.slice(Math.abs(currentIndex), copyChildren.length), ...firstChild ]
      }
      else {
        let lastChild = copyChildren.slice(copyChildren.length - Math.abs(currentIndex), copyChildren.length)
        copyChildren.splice(copyChildren.length - Math.abs(currentIndex), lastChild.length)
        newChildrenArr = [ ...lastChild, ...copyChildren ]
      }


      setSlideOption({
        ...slideOption,
        currentIndex: slideOption.currentIndex + (currentIndex),
        currentElementIndex: index
      })

      setCopyChildren(newChildrenArr)
      containerElement.style.marginLeft = ((slideOption.currentIndex + (currentIndex)) * (rootElement.offsetWidth / showElements) + 'px')

      setTimeout(() => containerElement.style.transition = 'left ease-in .3s', animationTime)
    }
  }

  return (
    <div id={ styles.root + ' ' + idRoot }>
      <div style={ { height: slideHeight, position: 'relative' } }>
        <div className={ styles.wrapp + ' ' + classNameContainer }>
          <div className={ styles.container }
               style={ {
                 left: -(slideOption.currentIndex * (rootElement.offsetWidth / showElements)) + 'px',
                 width: rootElement && (rootElement.offsetWidth / showElements) * copyChildren.length + 'px'
               } }>
            { copyChildren.map(item => item) }
          </div>
        </div>
        <div className={ styles.arrowControl }>
          <svg width="65" height="65" viewBox="0 0 65 65" fill="none" xmlns="http://www.w3.org/2000/svg"
               onClick={ changeSlide.bind(null, -1) }
          >
            <circle cx="32.5" cy="32.5" r="32.5" fill="#4D3E3D"/>
            <path fillRule="evenodd" clipRule="evenodd"
                  d="M40.3939 47.7127L36.278 51.8287L16.7576 32.3083L36.278 12.7878L40.3939 16.9038L24.9894 32.3083L40.3939 47.7127Z"
                  fill="white"/>
          </svg>
          <svg width="65" height="65" viewBox="0 0 65 65" fill="none" xmlns="http://www.w3.org/2000/svg"
               onClick={ changeSlide.bind(null, 1) }
          >
            <circle r="32.5" transform="matrix(-1 0 0 1 32.5 32.5)" fill="#4D3E3D"/>
            <path fillRule="evenodd" clipRule="evenodd"
                  d="M24.6061 47.7127L28.722 51.8287L48.2424 32.3083L28.722 12.7878L24.6061 16.9038L40.0106 32.3083L24.6061 47.7127Z"
                  fill="white"/>
          </svg>

        </div>
      </div>
      <div className={ styles.circleControl }>
        { copyChildren.map((item, key) =>
          <div key={ key + item }
               onClick={ setCurrentSlide.bind(null, key) }
               className={ slideOption.currentElementIndex === key ? styles.activeCircle : '' }/>
        ) }
      </div>
    </div>
  )
}

export default SlideShow
