import React, { useEffect } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { changeWindowSize } from "../store/window/actions"

const Root = ({ children, changeWindowSize }) => {

  useEffect(() => {
    changeWindowSize()
    document.getElementById('App').addEventListener('resize', () => {
      changeWindowSize()
    })
  }, [])

  return (
    <div id='App'>
      { children }
    </div>
  )
}


const mapStateToProps = state => {
  return {
    size: state.window.size
  }
}

const mapDispatchToProps = dispatch => ({
  changeWindowSize: bindActionCreators(changeWindowSize, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Root)
