import React, { useEffect } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { changeWindowSize } from "../store/window/actions"

const Root = ({ children, changeWindowSize, size }) => {

  useEffect(() => {
    changeWindowSize()
  }, [])

  return Object.keys(size).length ? { children } : null
}


const mapStateToProps = state => {
  return {
    size: state.window.size
  }
}

const mapDispatchToProps = dispatch => ({
  changeWindowSize: bindActionCreators(changeWindowSize, dispatch)
})

export default connect(mapStateToProps, mapDispatchToProps)(Root)
