import React, { useState, useEffect } from 'react'
import styles from './SlideShow.module.scss'

const SlideShow = ({ children, showElements }) => {
  const [ rootElement, setRootElement ] = useState({})

  useEffect(() => {
    setRootElement(document.getElementById(styles.root))
  }, [ rootElement ])

  return (
    <div id={ styles.root }>
      <div className={ styles.container } style={ { width: rootElement && (rootElement.offsetWidth / showElements) * children.length } }>
        { children }
      </div>
      <div className={ styles.arrowControl }>
        <svg width="65" height="65" viewBox="0 0 65 65" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="32.5" cy="32.5" r="32.5" fill="#4D3E3D"/>
          <path fillRule="evenodd" clipRule="evenodd" d="M40.3939 47.7127L36.278 51.8287L16.7576 32.3083L36.278 12.7878L40.3939 16.9038L24.9894 32.3083L40.3939 47.7127Z" fill="white"/>
        </svg>
        <svg width="65" height="65" viewBox="0 0 65 65" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle r="32.5" transform="matrix(-1 0 0 1 32.5 32.5)" fill="#4D3E3D"/>
          <path fillRule="evenodd" clipRule="evenodd" d="M24.6061 47.7127L28.722 51.8287L48.2424 32.3083L28.722 12.7878L24.6061 16.9038L40.0106 32.3083L24.6061 47.7127Z" fill="white"/>
        </svg>

      </div>
    </div>
  )
}

export default SlideShow
