import React, { useState } from 'react'
import styles from './Select.module.scss'

const Select = () => {
  const [ showItems, setShowItems ] = useState(false)
  const [ showIcon, setShowIcon ] = useState(false)
  return (
    <div className={ styles.root + ' ' + (showItems ? styles.rootActive : '') }>
      <div className={ styles.box }>
        <div className={ styles.container }>
          <div className={ styles.selectedItem } onClick={ this.dropDown }>
            { showIcon ?
              <Fragment>
                <span className={ 'flag flag-' + this.state.selectedItem.icon }/>
                { window.innerWidth < 926 || showTitle ?
                  <Fragment>
                    { maxCharacters > 0 && this.state.selectedItem[ itemKey ].length > maxCharacters ? this.state.selectedItem[ itemKey ].slice(0, maxCharacters) + "..." : this.state.selectedItem[ itemKey ] }
                  </Fragment>
                  : null }
              </Fragment> :
              !showIcon && this.state.selectedItem ?
                <Fragment>
                  { this.state.selectedItem[ itemKey ] ?
                    <Fragment>
                      { maxCharacters > 0 && this.state.selectedItem[ itemKey ].length > maxCharacters ? this.state.selectedItem[ itemKey ].slice(0, maxCharacters) + "..." : this.state.selectedItem[ itemKey ] }
                    </Fragment> : null
                  }
                </Fragment>
                : null }
          </div>
          <div className="select-box--arrow" onClick={ this.dropDown }>
            <span className={ `${ !this.state.showItems ? 'icon-shape' : 'icon-shape icon-shape-top' }` }/>
          </div>
        </div>

        <div
          className="select-box--items"
          style={ { display: this.state.showItems ? 'block' : 'none' } }>
          <PerfectScrollbar>
            {
              this.state.items.map((item, index) =>
                <div key={ index }
                     onClick={ () => this.selectItem(item) }
                     className={ this.state.selectedItem[ itemKey ] === item[ itemKey ] ? 'selected' : '' }>
                  { showIcon ? <span className={ 'flag flag-' + item.icon }/> : null }
                  { item[ itemKey ] }
                </div>)
            }
          </PerfectScrollbar>
        </div>

      </div>
    </div>
  )
}

export default Select
