import React, { useEffect } from 'react'
import styles from './Slot.module.scss'
import PropTypes from 'prop-types'

const Slot = ({ item, index }) => {
  const ref = React.createRef()

  useEffect(() => {
    console.log(ref)
  }, [ ref ])

  return (
    <div className={ styles.root + (item.isActive ? ' ' + styles.isActive : '') }
         custom-id={ index }
         style={{ height: item.isActive ? ref.current.childNodes[0].offsetHeight + ref.current.childNodes[1].offsetHeight : ref.current.childNodes[0].offsetHeight  }}
         ref={ ref }>
      <div className={ styles.title } custom-id={ index }>{ item.title } { index }</div>
      <div className={ styles.description } custom-id={ index }>{ item.description }</div>
    </div>
  )
}

Slot.propTypes = {
  item: PropTypes.object,
}

export default Slot
