import React from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from "redux"
import { changeValue } from "../store/test/actions"

const Index = ({ test }) => {
  console.log(test)
  return (
    <div id='home'>
      { test.value }
    </div>
  )
}

Index.getInitialProps = async ({ store, req }) => {
  const isServer = !!req
  if (isServer) {
    await store.dispatch(changeValue('Hello world'))
  }
  else store.dispatch(changeValue('Hello world'))
  return {}
}

const mapStateToProps = state => {
  return {
    test: state.test
  }
}

const mapDispatchToProps = dispatch => ({
  changeValue: text => dispatch(changeValue(text))
})

export default connect(mapStateToProps, mapDispatchToProps)(Index)
