import React from 'react'
import PropTypes from 'prop-types'
import Slot from "./Slot/Slot"
import styles from './DropDown.module.scss'

const DropDown = ({ items, isMoreColumn }) => {

  const handleClick = ({ target }) => {
    const id = target.getAttribute('custom-id')
    console.log(id)
  }

  return (
    <div className={ styles.root } onClick={ handleClick }>
      <div>
        { items.slice(0, (isMoreColumn ? Math.ceil(items / 2) : items.length)).map((item, index) =>
          <div key={ index } className={ styles.item }>
            <Slot item={ item } index={ index }/>
          </div>
        ) }
      </div>
      { isMoreColumn ?
        <div>
          { items.slice(Math.ceil(items / 2), items.length).map((item, index) =>
            <div key={ index } className={ styles.item }>
              <Slot item={ item } index={ index }/>
            </div>
          ) }
        </div> : null }
    </div>
  )
}

DropDown.propTypes = {
  items: PropTypes.arrayOf(PropTypes.object)
}

export default DropDown
