import React from 'react'
import { connect } from 'react-redux'
import SlideShow from "../../Layouts/SlideShow/SlideShow"
import AboutService from "./AboutService"

const AboutServiceRoot = ({ windowInnerWidth }) => {
  return (
    <div>
      { windowInnerWidth <= 1150 ?
        <SlideShow animationTime={ 300 }
                   showElements={ 3 }
                   slideHeight={ 232 }
                   classNameContainer={ 'aboutServiceSlider' }
                   idRoot={ 'aboutServiceSliderId' }
                   paddingRight={ 60 }
                   paddingLeft={ 60 }
        >
          { AboutService }
        </SlideShow> :
        <AboutService/>
      }
    </div>
  )
}

const mapStateToProps = state => {
  return {
    windowInnerWidth: state.window.size.windowInnerWidth
  }
}

export default connect(mapStateToProps)(AboutServiceRoot)
