import React, { useState, useEffect } from 'react'
import styles from './Select.module.scss'

const Select = ({ items, changeSelect = function () {}, name, itemKey, selected }) => {

  const [ showItems, setShowItems ] = useState(false)
  const [ selectedItem, setSelectedItem ] = useState('')
  let target

  useEffect(() => {
    document.getElementById('App').addEventListener('click', hideDropDown)
  }, [showItems])


  useEffect(() => {
    setSelectedItem(JSON.parse(JSON.stringify(selected || items[0])))
  }, [selected, items])

  const dropDown = (event) => {
    target = event.target
    setShowItems(!showItems)
  }

  const hideDropDown = (event) => {
    if (event.target !== target) {
      setShowItems(false)
      document.getElementById('App').removeEventListener('click', hideDropDown)
    }
  }

  const selectItem = (item) => {
    setSelectedItem(item)
    setShowItems(false)

    try {
      changeSelect(item, name)
    } catch (err) {
      return console.log(err)
    }
  }

  return (
    <div className={ styles.root + ' ' + (showItems ? styles.rootActive : '') }>
      <div className={ styles.box }>
        <div className={ styles.container }>
          <div className={ styles.selectedItem } onClick={ dropDown }>
            { selectedItem[ itemKey ] ? selectedItem[ itemKey ] : null }
          </div>
          <div className="select-box--arrow" onClick={ dropDown }>
            <span className={ `${ !showItems ? 'icon-shape' : 'icon-shape icon-shape-top' }` }/>
          </div>
        </div>

        <div
          className="select-box--items"
          style={ { display: showItems ? 'block' : 'none' } }>
          {
            items.map((item, index) =>
              <div key={ index }
                   onClick={ () => selectItem(item) }
                   className={ selectedItem[ itemKey ] === item[ itemKey ] ? styles.selected : '' }>
                { item[ itemKey ] }
              </div>)
          }
        </div>

      </div>
    </div>
  )
}

export default Select
